$(document).ready(function(){var o=$(".blog-masonry-container");o.imagesLoaded(function(){o.isotope({itemSelector:".blog-masonry-item",layoutMode:"masonry"})}),$(".blog-filters li").click(function(){var o=$(this);o.siblings("li").removeClass("active"),o.addClass("active");var a=o.attr("data-filter"),i=o.closest(".blog-masonry").find(".blog-masonry-container");i.isotope({filter:a})})});